<?php

namespace AppBundle\Repository;

/**
 * ModuleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ModuleRepository extends \Doctrine\ORM\EntityRepository
{
    public function getModules()
    {
        $qb = $this->getEntityManager()->createQueryBuilder()
            ->select('m, CONCAT(cc.course, cy.initials) course')
            ->from('AppBundle:Module', 'm')
            ->join('m.course_cycle', 'cc')
            ->join('cc.cycle', 'cy');


        return $qb->getQuery()->getArrayResult();
    }

    public function getAllModules()
    {
        return $this->findBy(array(), array('initials' => 'ASC'));
    }

    public function getHoursByCourseCycle($courseCycle, $schoolYear)
    {
        $sumHours = 0;
        $sumHoursDesdoble = 0;
        $arrayResult = array();

        $qb = $this->getEntityManager()->createQueryBuilder()
            ->select('DISTINCT m')
            ->from('AppBundle:Module', 'm')
            ->join('m.distributions_module_teacher', 'dmt')
            ->join('dmt.schoolYear', 'sy')
            ->where("m.course_cycle=:courseCycle")
            ->andWhere('sy.course=:schoolYear')
            ->setParameter('courseCycle', $courseCycle)
            ->setParameter('schoolYear', $schoolYear);

        $result = $qb->getQuery()->getArrayResult();

        foreach ($result as $item) {
            $sumHours += $item["hours"];
            $sumHoursDesdoble += $item["hoursDesdoble"];
        }

        $arrayResult[0]["hours"] = $sumHours;
        $arrayResult[0]["hoursDesdoble"] = $sumHoursDesdoble;

        return $arrayResult;
    }

    public function getModulesBySchoolYear($currentSchoolYear)
    {
        $qb = $this->getEntityManager()->createQueryBuilder()
            ->select('m')
            ->from('AppBundle:Module', 'm')
            ->join('m.distributions_module_teacher', 'dmt')
            ->where("dmt.schoolYear=:current_schoolYear")
            ->setParameter('current_schoolYear', $currentSchoolYear);


        return $qb->getQuery()->getArrayResult();
    }

    public function getTotalHoursSchoolYear($schoolYear)
    {
        $sumHours = 0;
        $sumHoursDesdoble = 0;
        $arrayResult = array();

        $qb = $this->getEntityManager()->createQueryBuilder()
            ->select('DISTINCT m')
            ->from('AppBundle:Module', 'm')
            ->join('m.distributions_module_teacher', 'dmt')
            ->join('dmt.schoolYear', 'sy')
            ->andWhere('sy.course=:schoolYear')
            ->setParameter('schoolYear', $schoolYear);

        $result = $qb->getQuery()->getArrayResult();

        foreach ($result as $item) {
            $sumHours += $item["hours"];
            $sumHoursDesdoble += $item["hoursDesdoble"];
        }

        $arrayResult[0]["hours"] = $sumHours;
        $arrayResult[0]["hoursDesdoble"] = $sumHoursDesdoble;

        return $arrayResult;
    }
}
